import React, { Component } from "react";
import { joi } from "joi-browser";
class Login extends Component {
  state = {
    username: "",
    password: "",
    errors: {},
  };
  schema = {
    username: joi.string().required(),
    password: joi.string().required(),
  };
  // username = React.useRef();
  onhandlesubmit = (e) => {
    e.preventDefault();
    const errorx = this.validate();
    if (errorx) return console.log("errors is here"); // if we found errors
    console.log("ahmed");
  };
  validate = () => {
    const errors = {};
    if (this.state.username.trim() === "") {
      errors.username = "username is required";
    }
    // if (this.state.password.trim() === "") {
    //   errors.password = "password is required";
    // }
    this.setState({ errors: errors });
    return Object.keys(errors).length === 0 ? null : errors;
  };
  handleChange = (e) => {
    console.log("e = ", e);
    let usersname = { ...this.state.username };
    // //edit
    // usersname = e.target.value;
    // this.setState({ usersname: usersname });
    usersname = e.target.value;
    this.setState({ username: usersname });
  };

  render() {
    console.log("errors = ", this.state.errors);
    return (
      <React.Fragment>
        <form onSubmit={this.onhandlesubmit}>
          <div className="mb-3">
            <label htmlFor="username" className="form-label">
              Email address
            </label>
            <input
              name="username"
              // ref={this.username}
              value={this.state.username}
              onChange={this.handleChange}
              type="email"
              className="form-control"
              id="username"
              aria-describedby="emailHelp"
            />
            <div id="emailHelp" className="form-text">
              We'll never share your email with anyone else.
            </div>
          </div>
          <div className="mb-3">
            <label htmlFor="password" className="form-label">
              Password
            </label>
            <input
              name="password"
              value={this.state.password}
              type="password"
              className="form-control"
              id="password"
            />
          </div>

          <button type="submit" className="btn btn-primary">
            Submit
          </button>
        </form>
      </React.Fragment>
    );
  }
}

export default Login;
