import React, { useRef, useEffect, useState } from "react";
import "./App.css";
import { Item } from "./item";
function App() {
  const ToDoList = [
    { id: 1, task: "Finish your tasks with nazmy" },
    { id: 2, task: "meeting with your manager" },
    { id: 3, task: "PS in CodeForce" },
  ];
  const [state, setState] = useState(ToDoList);
  const [itemlist, updateitemlist] = useState([]);
  const todo = useRef();

  function onhandlesubmit(e) {
    e.preventDefault();
    console.log(todo.current.value);

    let task = todo.current.value;
    if (task !== "") {
      let newlist = state;
      let id = Math.random();
      newlist.push({ id, task });
      setState({ newlist });
      console.log(newlist);
    }

    /////////////////////////
  }
  useEffect(() => {
    // newlist.push({ id, task });
    // setState({ state: newlist });
  });

  return (
    <div>
      <header>
        <div className="main">
          <h1 className="the-title">To-DO List</h1>
          <form>
            <input type="text" className="search" placeholder="search" />
          </form>

          <div className="main query">
            <ul>
              <Item state={state} />
            </ul>
          </div>
          <div>
            <form onSubmit={onhandlesubmit}>
              <input
                ref={todo}
                type="text"
                className="add"
                placeholder="add to-do"
              />
            </form>
          </div>
        </div>
      </header>
    </div>
  );
}

export default App;
