import React, { useRef, useEffect } from "react";
import "./App.css";
function App() {
  const todo = useRef();
  const ul = useRef();

  useEffect(() => {});
  function onhandlesubmit(e) {
    e.preventDefault();
    console.log(todo.current.value);
    let newtodolist = todo.current.value;
    /////////////////////////
  }

  return (
    <div>
      <header>
        <div className="main">
          <h1 className="the-title">To-DO List</h1>
          <form>
            <input type="text" className="search" placeholder="search" />
          </form>

          <div className="main query">
            <ul ref={ul}>
              <li>
                <span>ahmed</span>
                <span>
                  <i className="far fa-trash-alt delete"></i>
                </span>
              </li>
              <li>
                <span>mohamed</span>
                <span>
                  <i className="far fa-trash-alt delete"></i>
                </span>
              </li>
              <li>
                <span>sayed</span>
                <span>
                  <i className="far fa-trash-alt delete"></i>
                </span>
              </li>
            </ul>
          </div>
          <div>
            <form onSubmit={onhandlesubmit}>
              <input
                ref={todo}
                type="text"
                className="add"
                placeholder="add to-do"
              />
            </form>
          </div>
        </div>
      </header>
    </div>
  );
}

export default App;
