import React, { useState, useEffect, useRef, useCallback } from "react";
import { Button } from "./header";
function App() {
  const [counter, setcounter] = useState(0);
  const [title, setTitle] = useState("");
  const amountRef = useRef();
  const ReactMaxref = useRef(false);
  const listOfcount = [1, 4, 6, 10, 50];
  // function countup() {
  //   if (!ReactMaxref.current) {
  //     console.log(amountRef.current);
  //   }
  //   setcounter((c) => +amountRef.current.value + 1);
  // }
  const handletitle = () => {
    setTitle("ahmed in title");
  };

  useEffect(() => {
    // console.log("use ya man 1");
    document.title = title;
  }, [title]);
  useEffect(() => {
    // console.log("use ya man 2");
    document.title = `you have clicked ${counter} items`;
  }, [counter]);
  useEffect(() => {
    amountRef.current.focus();
  }, []);
  const Onclick = useCallback(() => {
    setcounter((c) => c + 1);
  }, [setcounter]);
  return (
    <div className="container text-center">
      <input ref={amountRef} className="m-5" />
      <br />
      {listOfcount.map((count) => (
        <Button counterfn={Onclick} label={count} key={count} />
      ))}
      <Button counterfn={Onclick} label="count Up" />;
      <button className="m-3" onClick={handletitle}>
        Change title
      </button>
      <h1 className="m-3">{counter}</h1>
    </div>
  );
}

export default App;
