import React, { useRef, useEffect, useState } from "react";
import "./App.css";
import { Item } from "./item";
function App() {
  const ToDoList = [
    { id: 1, task: "Finish your tasks with nazmy" },
    { id: 2, task: "meeting with your manager AbdelRhman" },
    { id: 3, task: "PS in CodeForce with osama khalil" },
  ];
  const [state, setState] = useState();
  const [itemlist, updateitemlist] = useState(ToDoList);
  const todo = useRef();

  function onhandlesubmit(e) {
    e.preventDefault();
    console.log(todo.current.value);
    setState(todo.current.value);
  }
  useEffect(() => {
    let id = Math.random();
    updateitemlist([...itemlist, { id: id, task: state }]);
  }, [state]);

  return (
    <div>
      <header>
        <div className="main">
          <h1 className="the-title">To-DO List</h1>
          <form>
            <input type="text" className="search" placeholder="search" />
          </form>

          <div className="main query">
            <ul>
              <Item items={itemlist} />
            </ul>
          </div>
          <div>
            <form onSubmit={onhandlesubmit}>
              <input
                ref={todo}
                type="text"
                className="add"
                placeholder="add to-do"
              />
            </form>
          </div>
        </div>
      </header>
    </div>
  );
}

export default App;
