import React, { useRef, useEffect, useState } from "react";
import "./App.css";
function App() {
  const ToDoList = [
    { id: 1, task: "Finish your tasks with nazmy" },
    { id: 2, task: "meeting with your manager" },
    { id: 3, task: "PS in CodeForce" },
  ];
  const [state, setState] = useState(ToDoList);
  const todo = useRef();
  const ul = useRef();

  function onhandlesubmit(e) {
    e.preventDefault();
    console.log(todo.current.value);

    let newtodolist = todo.current.value;
    if (newtodolist !== "") {
      let newlist = state;
      let id = Math.random();
      newlist.push({ id, newtodolist });
      console.log(newlist);
      setState({ state: newlist });
    }

    /////////////////////////
  }
  useEffect(() => {});

  return (
    <div>
      <header>
        <div className="main">
          <h1 className="the-title">To-DO List</h1>
          <form>
            <input type="text" className="search" placeholder="search" />
          </form>

          <div className="main query">
            <ul ref={ul}>
              {state.map((e) => (
                <li key={e.id}>
                  <span>{e.task}</span>
                  <span>
                    <i className="far fa-trash-alt delete"></i>
                  </span>
                </li>
              ))}
              {/* <li>
                <span>ahmed</span>
                <span>
                  <i className="far fa-trash-alt delete"></i>
                </span>
              </li>
              <li>
                <span>mohamed</span>
                <span>
                  <i className="far fa-trash-alt delete"></i>
                </span>
              </li>
              <li>
                <span>sayed</span>
                <span>
                  <i className="far fa-trash-alt delete"></i>
                </span>
              </li> */}
            </ul>
          </div>
          <div>
            <form onSubmit={onhandlesubmit}>
              <input
                ref={todo}
                type="text"
                className="add"
                placeholder="add to-do"
              />
            </form>
          </div>
        </div>
      </header>
    </div>
  );
}

export default App;
