import React, { useState } from "react";
import "./App.css";
import { Item } from "./item";

function App() {
  /// The Base App
  const ToDoList = [
    { id: 1, task: "finish your tasks with nazmy" },
    { id: 2, task: "meeting with your manager AbdelRhman" },
    { id: 3, task: "PS in CodeForce with osama khalil" },
  ];
  ////////  using useState to Store Data
  const [itemlist, SetItemList] = useState(ToDoList);
  const [state, setState] = useState(null);
  ////// local storage
  localStorage.setItem("localData", itemlist);
  let storage = localStorage.getItem("localData");

  console.log(storage);

  const OnSearchHandler = (e) => {
    // search input Function
    let searchTerm = e.target.value.trim().toLowerCase();
    if (searchTerm === "") {
      console.log("the search");
      SetItemList(ToDoList);
    } else {
      let filteredList = ToDoList.filter((item) =>
        item.task.toLowerCase().includes(searchTerm.toLowerCase())
      );
      console.log("itemlist", filteredList);
      console.log("searchTerm", searchTerm);

      SetItemList(filteredList);
    }
  };
  const onChangeHandler = (e) => {
    // pass data from input data tag to state
    setState(e.target.value);
  };
  const onhandlesubmit = (e) => {
    // Submit Data Function
    e.preventDefault();
    let id = Math.random();
    SetItemList([...itemlist, { id, task: state }]);
  };
  const onclickhandler = (item) => {
    // Delete Function
    let changeitems = [...itemlist];
    changeitems = changeitems.filter((e) => e.id !== item.id);
    SetItemList(changeitems);
  };

  return (
    <div>
      <header>
        <div className="main">
          <h1 className="the-title">To-DO List</h1>
          <input
            type="text"
            className="search"
            placeholder="Search"
            onChange={OnSearchHandler}
          />
          <div className="main query">
            <ul>
              <Item items={itemlist} ONCliCK={onclickhandler} />
            </ul>
          </div>
          <div>
            <form onSubmit={onhandlesubmit}>
              <input
                onChange={onChangeHandler}
                type="text"
                className="add"
                placeholder="Add To-Do"
              />
            </form>
          </div>
        </div>
      </header>
    </div>
  );
}

export default App;
